const href = window.location.href;
const encodedOgImagePath = encodeURIComponent(Inputs.ogImage);
const encodedTwitterImagePath = encodeURIComponent(Inputs.twitterImage);

const metaTags = {
  // Basic title and description for search engines
  'title': Inputs.title,
  'description': Inputs.description,
  // Open Graph values, used by Facebook, LinkedIn, etc.
  'og:title': Inputs.ogTitle,
  'og:description': Inputs.ogDescription,
  'og:image': href + encodedOgImagePath,
  // Helps social media platforms display the image correctly
  'og:image:height': Inputs.ogImageHeight,
  'og:image:width': Inputs.ogImageWidth,
  // Tells the service what kind of content is on the page
  // Possible values: website, article, book, profile, video.movie, video.episode, video.tv_show, video.other, music.song, music.album, music.playlist, music.radio_station
  'og:type': Inputs.ogType,
  // Used to force recognition of a specified url no matter what paths or parameters the shared URL is composed of
  'og:url': Inputs.ogUrl,
  // A comma separated list of values that tells crawlers how to index the site
  // Possible values: see footnote comment below **
  'robots': Inputs.robots,
  // Values specifically for Twitter
  'twitter:title': Inputs.twitterTitle,
  'twitter:description': Inputs.twitterDescription,
  // Type of content to tell twitter how to present the content when shared in a tweet
  // Possible values: summary, summary_large_image, app (mobile app promotion), player (shares video or audio content directly in the tweet)
  'twitter:card': Inputs.twitterCard,
  'twitter:image': href + encodedTwitterImagePath
};

Object.keys(metaTags).forEach(key => {
  Noodl.SEO.setMeta(key, metaTags[key]);
});


/*
** Possible robots metatag values: 

index: Allows search engines to index the page (i.e., include it in their search results). This is the default behavior even if the tag isn’t present.

noindex: Prevents search engines from indexing the page (i.e., the page will not appear in search results).

follow: Instructs search engines to follow the links on the page, potentially discovering new pages to index. Like "index", this is default behavior.

nofollow: Prevents search engines from following any links on the page. This means that while the page itself may be indexed, the search engine won’t use the links on that page to discover new pages.

noarchive: Tells search engines not to store a cached copy of the page.

nosnippet: Prevents a text snippet or video preview from being shown in the search results.

noodp: Blocks search engines from using metadata from the Open Directory Project (DMOZ) for titles or snippets for the page. Note: As the DMOZ is no longer active, this directive is essentially obsolete.

notranslate: Advises search engines not to offer a translation for this page in search results.

noimageindex: Instructs search engines not to index images on the page. Images might still appear in search results if they're linked from elsewhere.

unavailable_after: Specifies a date/time after which the page should not be indexed. For example, unavailable_after: 25-Jun-2020 15:00:00 EST.
*/

