parameters.functionScript:
const html = Inputs.Html;
const text = Inputs.Text;
const to = Inputs.To;
const cc = Inputs.CC;
const bcc = Inputs.BCC;
const from = Inputs.From;
const subject = Inputs.Subject;
const apiKey = Inputs.APIKey;

if(apiKey === undefined || apiKey === "") {
    Outputs.Failure()
    throw Error("No API Key provided!")
}

const personalizations = [
    { "to": (Array.isArray(to)?to.map(e => ({email:e}) ):[{"email": to}]) }
]

if(cc !== undefined) {
    personalizations[0]["cc"] = (Array.isArray(cc)?cc.map(e => ({email:e}) ):[{"email": cc}])
}

if(bcc !== undefined) {
    personalizations[0]["bcc"] = (Array.isArray(bcc)?bcc.map(e => ({email:e}) ):[{"email": bcc}]) 

}

const res = await fetch("https://api.sendgrid.com/v3/mail/send",{
    method:'POST',
    headers:{
        "Authorization":"Bearer " + apiKey,
        "Content-Type":"application/json"
    },
    body:JSON.stringify({
    personalizations,
    "from": {"email":from},
    "subject": subject,
    "content": (html!==undefined)?
     [{"type": "text/html", "value": html}]:
    [{"type": "text/plain", "value": text}]

    })
})

if(res.ok) {
    Outputs.Success()
}
else {
    Outputs.Failure()
    const json = await res.json()
    throw Error(json.errors.map(e => e.message).join('\n'))
}

if(Inputs.Domain === undefined) {
    throw Error("You must specify the domain your application is deployed to for email links to work.")
}

Outputs.Content = Inputs.Content.replaceAll('$Domain',Inputs.Domain)

const user = Noodl.Users.Current

// Check if email is already verified
if(user.Properties.emailVerified) {
    Outputs.EmailIsVerified()
    return;
}

// Generate token and expiry date
user.Properties.emailVerified = false;
user.Properties.emailVerificationToken = Math.random().toString(26).slice(2);
user.Properties.emailVerificationTokenExpires = (new Date(Date.now() + 24*60*60*1000)).toISOString();
await user.save()

// Done
Outputs.Token = user.Properties.emailVerificationToken;
Outputs.Email = [user.Properties.email];
Outputs.Done()

const users = await Noodl.Records.query("_User",{
    username:{equalTo:Inputs.Email}
})

if(users.length === 0) {
    Outputs.Error = "No user with that email found."
    Outputs.Failure()
    return;
}

const user = users[0];

// Generate token and expiry date
user.passwordResetToken = Math.random().toString(26).slice(2)
user.passwordResetTokenExpires = (new Date(Date.now() + 24*60*60*1000)).toISOString()
await Noodl.Records.save(user)

// Done
Outputs.Token = user.passwordResetToken;
Outputs.Email = [Inputs.Email]
Outputs.Success()

if(Inputs.Token === undefined || Inputs.Email === undefined) {
    Outputs.Error = "Must supply token and email"
    Outputs.Failure()
    return
}

const users = await Noodl.Records.query("_User",{
    username:{equalTo:Inputs.Email}
})

if(users.length === 0) {
     Outputs.Error = "No user matched email."
     Outputs.Failure()
     return
}

const user = users[0]
if(user.passwordResetToken === Inputs.Token &&
    (new Date(user.passwordResetTokenExpires)).getTime() > Date.now()) {
    // Successful
    user.password = Inputs.NewPassword;
    user.passwordResetToken = null;
    user.passwordResetTokenExpires = null;
    await Noodl.Records.save(user)
    Outputs.Success()
}
else {
    Outputs.Error = "Invalid token."
    Outputs.Failure()
}

if(Inputs.Token === undefined || Inputs.Email === undefined) {
    Outputs.Error = "Must supply token and email"
    Outputs.Failure()
    return
}

const users = await Noodl.Records.query("_User",{
    username:{equalTo:Inputs.Email}
})

if(users.length === 0) {
     Outputs.Error = "No user matched email."
     Outputs.Failure()
     return
}

const user = users[0]
if(user.emailVerificationToken === Inputs.Token &&
    (new Date(user.emailVerificationTokenExpires)).getTime() > Date.now()) {
    // Successful
    await Noodl.Records.save(user,{
            emailVerified:true,
            emailVerificationToken:null,
            emailVerificationTokenExpires:null
    })

    Outputs.Success()
}
else {
    Outputs.Error = "Invalid token."
    Outputs.Failure()
}

// Decrease loading spinner running counter
Noodl.Variables.NumLoadingSpinnerRunning--

// When we reach zero, hide the loading spinner
if(Noodl.Variables.NumLoadingSpinnerRunning < 1)
    Noodl.Events.emit("Hide loader");

// If we have multiple show signals sent, only update the
// running count
if(Noodl.Variables.NumLoadingSpinnerRunning > 0)
    Noodl.Variables.NumLoadingSpinnerRunning++
else {
    // If this is the first show signal, show the popup
    Noodl.Variables.NumLoadingSpinnerRunning = 1
    Outputs.ShowPopup()
}

// Make sure you can click through parent div
// so it doesn't block like normal popups
Inputs.This.getDOMElement().style.pointerEvents = 'none'
Inputs.This.getDOMElement().parentElement.style.pointerEvents = 'none'
